import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import Navbar from '../components/Navbar';
import axios from 'axios';

const Dashboard = () => {
  const { user } = useAuth();
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('/api/dashboard', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setDashboardData(response.data);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div>
      <Navbar />
      <div className="container">
        <h1>Welcome, {user?.username}!</h1>

        <div className="dashboard-stats">
          <div className="stat-card">
            <h3>Total Medicines</h3>
            <p>{dashboardData?.totalMedicines || 0}</p>
          </div>
          <div className="stat-card">
            <h3>Low Stock</h3>
            <p>{dashboardData?.lowStockCount || 0}</p>
          </div>
          <div className="stat-card">
            <h3>Expired</h3>
            <p>{dashboardData?.expiredCount || 0}</p>
          </div>
          <div className="stat-card">
            <h3>Total Reminders</h3>
            <p>{dashboardData?.totalReminders || 0}</p>
          </div>
        </div>

        <div className="quick-actions">
          <Link to="/add-medicine" className="btn btn-primary">Add Medicine</Link>
          <Link to="/stock-overview" className="btn btn-secondary">View Stock</Link>
          <Link to="/notifications" className="btn btn-info">Notifications</Link>
        </div>

        {dashboardData?.lowStock?.length > 0 && (
          <div className="alerts">
            <h3>Low Stock Alerts</h3>
            {dashboardData.lowStock.map(medicine => (
              <div key={medicine._id} className="alert">
                {medicine.name} - Only {medicine.stockCount} left
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
