import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar';
import axios from 'axios';

const PatientDashboard = () => {
  const [dashboardData, setDashboardData] = useState(null);
  const [notes, setNotes] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
    fetchNotes();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('/api/dashboard', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setDashboardData(response.data);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchNotes = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('/api/dashboard/notes', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setNotes(response.data.notes);
    } catch (error) {
      console.error('Error fetching notes:', error);
    }
  };

  const saveNotes = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.post('/api/dashboard/notes', { notes }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert('Notes saved successfully!');
    } catch (error) {
      console.error('Error saving notes:', error);
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div>
      <Navbar />
      <div className="container">
        <h1>Patient Dashboard</h1>

        <div className="patient-overview">
          <div className="medicines-today">
            <h3>Today's Medicines</h3>
            {dashboardData?.medicines?.map(medicine => (
              <div key={medicine._id} className="medicine-item">
                <span>{medicine.name} - {medicine.dosage}</span>
                <span>Time: {medicine.time}</span>
              </div>
            ))}
          </div>

          <div className="notes-section">
            <h3>Personal Notes</h3>
            <textarea
              value={notes}
              onChange={(e) => setNotes(e.target.value)}
              placeholder="Add your personal notes here..."
              rows="6"
            />
            <button onClick={saveNotes} className="btn btn-primary">Save Notes</button>
          </div>
        </div>

        <div className="recent-notifications">
          <h3>Recent Notifications</h3>
          {dashboardData?.notifications?.map(notification => (
            <div key={notification._id} className="notification-item">
              <h4>{notification.title}</h4>
              <p>{notification.message}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default PatientDashboard;
