import React, { useState } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const Navbar = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();
  const [showProfileModal, setShowProfileModal] = useState(false);

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const isActive = (path) => location.pathname === path;

  const getInitials = (user) => {
    if (user?.firstName && user?.lastName) {
      return `${user.firstName[0]}${user.lastName[0]}`.toUpperCase();
    }
    return user?.username ? user.username[0].toUpperCase() : 'U';
  };

  const ProfileModal = () => (
    <div className="modal-overlay" onClick={() => setShowProfileModal(false)}>
      <div className="modal" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h2 className="modal-title">Profile Details</h2>
          <button 
            className="modal-close"
            onClick={() => setShowProfileModal(false)}
          >
            Ã—
          </button>
        </div>

        <div className="profile-details">
          <div className="profile-avatar">
            <div className="profile-icon large">
              {getInitials(user)}
            </div>
          </div>

          <div className="profile-info">
            <div className="info-item">
              <span className="info-label">Name:</span>
              <span className="info-value">
                {user?.firstName && user?.lastName 
                  ? `${user.firstName} ${user.lastName}` 
                  : 'Not provided'
                }
              </span>
            </div>

            <div className="info-item">
              <span className="info-label">Username:</span>
              <span className="info-value">{user?.username}</span>
            </div>

            <div className="info-item">
              <span className="info-label">Email:</span>
              <span className="info-value">{user?.email}</span>
            </div>

            <div className="info-item">
              <span className="info-label">User Type:</span>
              <span className="info-value">{user?.userType}</span>
            </div>

            {user?.age && (
              <div className="info-item">
                <span className="info-label">Age:</span>
                <span className="info-value">{user.age}</span>
              </div>
            )}
          </div>
        </div>

        <div className="form-actions">
          <Link 
            to="/profile" 
            className="premium-btn btn-outline"
            onClick={() => setShowProfileModal(false)}
          >
            Edit Profile
          </Link>
          <button 
            className="premium-btn btn-secondary"
            onClick={handleLogout}
          >
            Logout
          </button>
        </div>
      </div>
    </div>
  );

  return (
    <>
      <nav className="navbar">
        <div className="navbar-content">
          <Link to="/dashboard" className="navbar-brand">
            Medi Tracker
          </Link>

          <div className="navbar-nav">
            <Link to="/dashboard" className="nav-link">Dashboard</Link>
            <Link to="/add-medicine" className="nav-link">Add Medicine</Link>
            <Link to="/stock-overview" className="nav-link">Stock Overview</Link>
            <Link to="/notifications" className="nav-link">Notifications</Link>

            <div 
              className="profile-icon"
              onClick={() => setShowProfileModal(true)}
              title="Profile"
            >
              {getInitials(user)}
            </div>
          </div>
        </div>
      </nav>

      {showProfileModal && <ProfileModal />}
    </>
  );
};

export default Navbar;
